hexo.extend.tag.register('sidebar-link', function(args, content, options){
  var id = args[0];
  var title = (args.length > 1) ? args[1] : "<sup>&dagger;</sup>";
  return '<a class="sidebar-link" id="sidebar-link-' + id + '">' + title + '</a>';
});

hexo.extend.tag.register('sidebar', function(args, content, options){
  var id = args[0];
  var engine = 'markdown';
  if(args.length > 1) engine = args[1];
  var rcontent = hexo.render.renderSync({text:content, engine: engine});
  return '<aside class="sidebar sidebar-hidden" id="sidebar-' + id + '">' + rcontent + '</aside>';
}, { ends: true });
